import React from "react"
import {connect} from "react-redux"
import {handleAddGoal, removeGoal} from "../actions/goals"
import List from "./List"


class Goals extends React.Component{

    addItem = e => {
       e.preventDefault();
        
       this.props.dispatch(handleAddGoal(this.input.value, () => this.input.value = ""))
       };
       removeItem = (goal) => {
           this.props.dispatch(removeGoal(goal.id))
       }
 render() {
     return(
         <div>
         <h1>Goals</h1>
         <input
            type="text"
            placeholder="Add Goals"
            ref={(input) => this.input = input}
        />
         <button onClick={this.addItem} >Add Goal</button>
         <List remove={this.removeItem} items={this.props.goals}/>
         </div>
     )
 }
}

export default connect((state) => ({
 goals: state.goals
}))(Goals)
