import React from "react"

class List extends React.Component { 
  
  state ={
    all: true,
    active: false,
    tocomplete: false
  }
  showCompleted = ()=> {
    this.setState({all: false, active: false, tocomplete: true})
  }
  
  showAll = ()=> {
    this.setState({all: true, active: false, tocomplete: false})
  }
  showActive = ()=> {
    this.setState({all: false, active: true, tocomplete: false})
  }
  
render() {
  //const {items, toggle, remove} = this.props
  const completed = this.props.items.filter(item => item.complete === true)
  const activeItems = this.props.items.filter(item => item.complete !== true)
  const {all, active, tocomplete} = this.state
  return (
    <div>
    {/*<p>
      {JSON.stringify(this.props.items)}
    </p>*/}
      <ol>
        {this.props.items.map(item => (
          item.name !== ""?
          <li key={item.id} 
          style = {{display: all === true? "block" : "none"}}>
          <button 
            onClick={() => this.props.toggle && this.props.toggle(item.id)}
          >W</button>
          <span
            style = {{textDecoration: item.complete? "line-through" : "none"}}
            > {item.name.replace(/^./, item.name[0].toUpperCase())} </span>
          <button onClick={() => this.props.remove(item)}>X</button>
          </li>
          :""
          )
        )}
        {completed.map(item => (
          <li key={item.id} style = {{display: tocomplete === true? "block" : "none"}}>
          <button 
            onClick={() => this.props.toggle && this.props.toggle(item.id)}
          >W</button>
          <span
            style = {{textDecoration: item.complete? "line-through" : "none"}}
            > {item.name.replace(/^./, item.name[0].toUpperCase())} </span>
          <button onClick={() => this.props.remove(item)}>X</button>
          </li>
        )
        )}
        {activeItems.map(item => (
          <li key={item.id} style = {{display: active === true? "block" : "none"}}>
          <button 
            onClick={() => this.props.toggle && this.props.toggle(item.id)}
          >W</button>
          <span
            style = {{textDecoration: item.complete? "line-through" : "none"}}
            > {item.name.replace(/^./, item.name[0].toUpperCase())} </span>
          <button onClick={() => this.props.remove(item)}>X</button>
          </li>
        )
      )}
    </ol>
    <div className="bottom-navs">
      {this.props.items.length !== 0?
        <p>
          <span style = {{display: all === true? "block" : "none"}}>
            {activeItems.length + (
              activeItems.length === 1
              ?" Item Left"
              :" Items Left"
            )}
          </span>
          <span style = {{display: tocomplete === true? "block" : "none"}}>
            {completed.length + (
              completed.length === 1
              ?" Item Left"
              :" Items Left"
            )}
          </span>
          <span style = {{display: active === true? "block" : "none"}}>
            {activeItems.length + (
              activeItems.length === 1
              ?" Item Left"
              :" Items Left"
            )}
          </span>
          <button onClick={this.showAll}>All</button>
          <button onClick={this.showActive}>Active</button>
          <button onClick={this.showCompleted}>Completed</button>
          <a href="2.e">Clear Completed</a>
        </p>
        : ""
        }
    </div>
</div> 
  )}
}

export default List
