import {ADD_TODO} from "../actions/todos"

const comments = (store) => (next) => (action) => {

    if(action.type === ADD_TODO && action.todo.name !== "") {
         alert("Don't forget to "+ action.todo.name)
    }else{
        return next({})
    }
    return next(action)
}

export default comments;
