export const ADD_GOAL = "ADD_GOAL";
export const REMOVE_GOAL = "REMOVE_GOAL";


export const addGoal = (goal) => { 
    return{
        type: "ADD_GOAL",
        goal
    }}
export const removeGoal = (id) => { 
    return{
        type: "REMOVE_GOAL",
        id
    }}
